// Tomy Typography__________________________

//TYPOGRAPHIC SCALE_______________________________________________________
h1, h2, h3, h4, p, .small_text { 
    margin: .5em 0;
    color: $color-light-accent; 
}  

h1, h2, h3{
font-family: $heading-condensed-font-family; 
text-transform: uppercase;
}

.small_text, .caption{
    font-family: $heading-normal-font-family;
    text-transform: uppercase;
}

h4, p{
font-family: $bodyfont-family;
}


// _____

h1 {
font-size: 3em;
line-height: 1;
font-weight: 700;
letter-spacing: .02em;
}

h2{
margin-top: .5em;
margin-bottom: 0;
font-size: 3.5rem;
