// Colors
$color-dark-accent: black;
$color-light-accent: white;
$color-background-teal:#8fd4c2;
$color-background-darkteal: #255c5c;

// Breakpoints
$breakpoint-mobile: 767px;
$breakpoint-small: 768px;
$breakpoint-medium: 1200px;
$breakpoint-large: 1550px;
$breakpoint4k: 2560px;
// Spacing
$section-padding: 15vh 0;

//TYPOGRAPHY
$headingfont-family: 'Open Sans Condensed', 'Bebas Neue', 'Franklin Gothic Condensed', 'Arial', sans-serif;
$bodyfont-family: 'Lora', serif;


/* HEADING*/
h1, h2, h3 { 
    margin: .5em 0;
    font-weight: 100;
   line-height: 1;
    color: $color-light-accent; 
  }  

  h1{
    font-family: $headingfont-family; 
  }

  h2, h3, p{
    font-family: $bodyfont-family;
  }

  h1 {
    font-size: 3em;
    line-height: 1;
    font-weight: 700;
    color:$color-light-accent;
    text-transform: uppercase;
    letter-spacing: .02em;
  }

  h2{
    text-transform: none;
    margin-top: .5em;
    margin-bottom:.2em;
    font-size: 2em;
    line-height: 1.4;
    font-weight: 700;
    color:$color-dark-accent; 
  }

  h3 {

    font-size: 1.5em;
    line-height: 1.4;
    font-weight: 100;
    text-transform: uppercase;
    color: $color-light-accent; 
    letter-spacing: .2em;
  }



  p {
    font-size: 1em;
  font-weight: 100;
  font-style: normal;
  }


// img__________
img{
  width: 100%;
  height: auto;
}




//   HERO______________

#hero {
    height: 100vh;
	display: grid;
	place-items: center;
	// background: $color-dark-accent;


	.hero-content {
		h1 {
            font-size: 5rem;
            margin-bottom: 0;
            text-align: center;
		}

		h3 {
      margin-top: 0;
            font-size: 2rem;
            text-align: center;
		}
	}

	@media (min-width: $breakpoint-medium) {
		.hero-content {
			position: relative;
			// top: 20%;
			// left: 20%;

			h1 {
				font-size: 10vw;
			}
			h3 {
				font-size: 2.5vw;
			}
		}
	}
}


// Base
html {
    box-sizing: border-box;
  }

  *, 
  *:before, 
  *:after {
    box-sizing: inherit;
  }

body{
  position: relative;
  margin:0;
    // animation for background color________________

	background: linear-gradient(358deg, $color-background-teal, $color-background-darkteal, $color-dark-accent);
	background-size: 600% 600%;

	-webkit-animation: teal-background 200s ease infinite;
	-moz-animation: teal-background 200s ease infinite;
	animation: teal-background 200s ease infinite;


	@keyframes teal-background {
		0%{background-position:53% 0%}
		50%{background-position:48% 100%}
		100%{background-position:53% 0%}
  }
}


  // Fog
.fog {
    position: relative;
  margin:0;
  padding-top: 0;
  margin-top: 0;
    height: 100vh;
    width: 100%;



    &__container {
      position: absolute;
      height: 100%;
      width: 100%;

      overflow: hidden;
    }

    &__img {
      position: absolute;
      height: 100vh;
      width: 300vw;

      &--first {
        background: url('https://res.cloudinary.com/dkr52htco/image/upload/v1536173269/fog-1.png') repeat-x;
        background-size: contain;
        background-position: center;
        animation: marquee 120s linear infinite;

      }

      &--second {
        background: url('https://res.cloudinary.com/dkr52htco/image/upload/v1536173269/fog-2.png') repeat-x;
        background-size: contain;
        background-position: center;
        animation: marquee 100s linear infinite;

      }
    }
  }

  // Marquee
  @keyframes marquee {
    0% { transform: translate3d(0, 0, 0); }
    100% { transform: translate3d(-200vw, 0, 0); }
  }

