#students {
	padding: $section-padding;
	position: relative;
	padding-bottom: 60vh;

	.forest{
		width: 100%;
		height: auto;
		z-index: -100;
		position: absolute;
		bottom:0;
	}

	.container {
		display: grid;
		grid-gap: 50px;
		grid-template-columns: repeat(4, 1fr);
		grid-template-areas:
		". . animation animation"
		"interface interface nessy ."
		"rock_monster . . mer-skull";
		grid-row-gap: 150px;

		// .fairy-sit_container{
		// 	.fairy-sit{
		// 	grid-area: fairy-sit;
		// 	width: 20vw;
		// 	left: 10vw;
